mapper.select:
SELECT z.id, z.show_id AS showId, z.ziapi_location AS ziapiLocation, z.ancestors_name AS ancestorsName, z.zipai_txt AS zipaiTxt, z.visit_num AS visitNum, z.status, z.create_time AS createTime, z.create_user AS createUser, z.update_time AS updateTime, z.update_user AS updateUser FROM fan_sys_site s,fan_sys_web_news_show a,fan_news_culture_zipai z where 1=1
and s.family_code=#{familyCode}
and s.region_code=#{code}
and s.id=a.site_id and a.show_id=z.show_id
and z.`status`=#{status}
