mapper.select:
SELECT
'3' as source,f.id, f.show_id AS showId, f.person_name AS personName, f.person_summary AS personSummary, f.pic_file_src AS picFileSrc, f.pic_file_name AS picFileName, f.visit_num AS visitNum, f.status, f.create_time AS createTime, f.create_user AS createUser, f.update_time AS updateTime, f.update_user AS updateUser
FROM fan_sys_site s,fan_sys_web_news_show n,fan_sys_recommend m,fan_news_famous_person f
where
1=1

    and e.parent=#{parent}

and s.id=n.fan_sys_site_id and  n.show_id=m.show_id

    and m.`status`=#{status}


    and m.news_source=#{newsSource}

and m.show_id=f.show_id and m.news_id=f.id
