mapper.select:
SELECT
'4' as source,s.name as sizeName,m.id as recommendId,f.id, f.show_id AS showId, f.person_name AS personName,
f.person_summary AS personSummary, f.pic_file_src AS picFileSrc, f.pic_file_name AS picFileName, f.visit_num AS
visitNum, f.status, f.create_time AS createTime, f.create_user AS createUser, f.update_time AS updateTime,
f.update_user AS updateUser
FROM fan_sys_site s,fan_sys_web_news_show n,fan_sys_recommend m,fan_news_famous_person f
WHERE 1=1


    AND n.site_id=#{siteId}

AND n.parent_id in (SELECT n.menu_id FROM fan_sys_web_news_show n WHERE 1=1 AND n.site_id=#{siteId} AND
n.menu_code='famous')
AND s.id=n.site_id AND n.show_id=m.show_id

    AND m.status=#{status}


    AND m.is_auto=#{isAuto}


    AND m.news_source=#{newsSource}

AND m.show_id=f.show_id AND m.news_id=f.id

    AND f.person_name LIKE CONCAT('%',#{personName},'%')


    and right(m.show_id,2) ='21';


    and right(m.show_id,2) ='22';
