mapper.select:
select temp1.* from ( SELECT '1' as source,
from pro_news_culture_zipai n where 1=1
AND n.show_id=#{showId}
AND n.status=#{status}
AND n.zipai_txt LIKE CONCAT('%',#{zipaiTxt},'%')
union all SELECT '2' as source, z.id, z.show_id AS showId, z.ziapi_location AS ziapiLocation, z.ancestors_name AS ancestorsName, z.zipai_txt AS zipaiTxt, z.visit_num AS visitNum, z.status, z.create_time AS createTime, z.create_user AS createUser, z.update_time AS updateTime, z.update_user AS updateUser from fan_sys_site s,fan_sys_web_news_show n,fan_news_culture_zipai z where 1=1
AND s.parent=#{siteId}
AND s.id=n.site_id AND n.show_id=z.show_id
AND z.status=#{status}
AND z.zipai_txt LIKE CONCAT('%',#{zipaiTxt},'%')
) temp1 order by temp1.updateTime desc
